(103, 0, '2019-06-20 14:41:03', '2019-06-20 12:41:03', '<!-- wp:acf/title-text {\n    \"id\": \"block_5d35df6ca78ab\",\n    \"name\": \"acf\\/title-text\",\n    \"data\": {\n        \"title\": \"Le contexte\",\n        \"_title\": \"field_5d35df2a647f0\",\n        \"texte\": \"Concurrencé par de nombreux acteurs : grandes surface, magasins spécialisés ou encore pure players, le secteur du jouet est aujourd’hui un marché en grande difficulté. De plus, l’arrivée du Black Friday en France a réduit une période de pic saisonnier déjà très limité, qui s’étend désormais de novembre à décembre.\",\n        \"_texte\": \"field_5d35df32647f1\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/title-text {\n    \"id\": \"block_5d397eab16ecf\",\n    \"name\": \"acf\\/title-text\",\n    \"data\": {\n        \"title\": \"Les objectifs\",\n        \"_title\": \"field_5d35df2a647f0\",\n        \"texte\": \"Jouéclub est un groupe coopératif composé de 300 magasins et d’autant de sites web. Une situation complexe qui obligeait les consommateurs à choisir un site différent bien qu’ils acheteaient des produits de la société.\",\n        \"_texte\": \"field_5d35df32647f1\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->\n\n<!-- wp:acf/title-text {\n    \"id\": \"block_5d397ed516ed0\",\n    \"name\": \"acf\\/title-text\",\n    \"data\": {\n        \"title\": \"Le choix de Proximis\",\n        \"_title\": \"field_5d35df2a647f0\",\n        \"texte\": \"Retailer à l’origine, Jouéclub voulait une solution intégrant le web et les magasins.\",\n        \"_texte\": \"field_5d35df32647f1\"\n    },\n    \"align\": \"\",\n    \"mode\": \"edit\"\n} /-->', 'Joué Club', '', 'publish', 'closed', 'closed', '', 'joue-club', '', '', '2019-07-25 16:56:37', '2019-07-25 14:56:37', '', 0, 'http://proximis.local/?post_type=reference&#038;p=103', 0, 'reference', '', 0),
